package server

import (
	"github.com/gin-gonic/gin"
	"github.com/swaggo/files"
	"github.com/swaggo/gin-swagger"
	
	"github.com/Oloruntobi1/walletservice/docs" // docs is generated by Swag CLI, you have to import it.
)

