type Server struct {
	router *gin.Engine
}

func NewServer() *Server{
	s := &Server{}
	docs.SwaggerInfo.Title = "Wallet API"
	docs.SwaggerInfo.Description = "This is a sample wallet service."
	docs.SwaggerInfo.Version = "1.0"
	docs.SwaggerInfo.Host = "localhost:8080"
	docs.SwaggerInfo.BasePath = "/"
	docs.SwaggerInfo.Schemes = []string{"http", "https"}
	r := gin.Default()
	
	r.POST("/createwallet", s.HandleWalletCreate)
	r.POST("/payout", s.HandlePayout)
	
	r.GET("/swagger/*any", ginSwagger.WrapHandler(swaggerFiles.Handler))
	s.router = r
	return s
}

func(s *Server) Start(add string) error {
	return s.router.Run(add)
} 

